import { Entity, Column, PrimaryGeneratedColumn } from "typeorm";

@Entity("Products")
export class ProductsEntity {
  @PrimaryGeneratedColumn()
  id: number;

  @Column({nullable: true})
productId: string;

@Column({nullable: true})
vendorId: number;

@Column({nullable: true})
name: string;

@Column({nullable: true})
price: number;

@Column({nullable: true})
weight: number;

@Column({nullable: true})
description: string;

@Column({nullable: true})
thumbnail: string;

@Column({nullable: true})
image: string;

@Column({nullable: true})
category: number;

@Column({nullable: true})
createDate: Date;

@Column({nullable: true})
stock: number;


}
