import { Entity, Column, PrimaryGeneratedColumn } from "typeorm";

@Entity("Reviews")
export class ReviewsEntity {
  @PrimaryGeneratedColumn()
  id: number;

  @Column({nullable: true})
reviewId: string;

@Column({nullable: true})
productId: number;

@Column({nullable: true})
customerId: number;

@Column({nullable: true})
vendorId: number;

@Column({nullable: true})
rating: number;

@Column({nullable: true})
reviewDetails: string;

@Column({nullable: true})
date: Date;


}
